import React, { useState } from "react";
import { Link } from "react-router-dom";
import { useNavigate } from 'react-router-dom';
import axios from "axios";

export default function Login({
    username,
    password,
    setUsername, 
    setPassword, 
    handleLogin
}) {
    const navigate = useNavigate();

    const handleLoginSubmit = async (e) => {
        e.preventDefault();
        // Add your login logic here
        handleLogin();
    };

    return (
        <div className="container-fluid d-flex align-items-center justify-content-center" style={{ height: '100vh', background: 'linear-gradient(to right, #4A148C, #7E57C2)' }}>
            <div className="card shadow" style={{ width: '400px', borderRadius: '15px', overflow: 'hidden', background: 'white', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)' }}>
                <div className="card-body p-4">
                    <h3 className="text-center mb-4" style={{ color: '#4A148C' }}>Login</h3>
                    <form onSubmit={handleLoginSubmit}>
                        <div className="mb-3">
                            <label htmlFor="username" className="form-label">Username</label>
                            <input
                                type="text"
                                className="form-control"
                                id="username"
                                placeholder="Enter username"
                                onChange={(e) => setUsername(e.target.value)}
                                value={username}
                            />
                        </div>
                        <div className="mb-3">
                            <label htmlFor="password" className="form-label">Password</label>
                            <input
                                type="password"
                                className="form-control"
                                id="password"
                                placeholder="Enter password"
                                onChange={(e) => setPassword(e.target.value)}
                                value={password}
                            />
                        </div>

                        <div className="d-grid gap-2">
                            <button type="submit" className="btn btn-primary btn-block" style={{ background: '#4A148C' }}>
                                Login
                            </button>
                            <Link to="/register" className="btn btn-success btn-block" style={{ background: '#7E57C2' }}>
                                Register
                            </Link>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
}
