import React, { useState } from "react";
import { Link } from "react-router-dom";
import { useNavigate } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import axios from "axios";

// Import your background image (replace 'your-image.jpg' with your actual image file)
import backgroundImage from 'C:/Users/AMIR/Desktop/Temp-repo/client/src/components/AUTH_PAGES/img_6.jpg';

export default function Login({
    username,
    password,
    setUsername, 
    setPassword, 
    handleLogin
}) {
    const navigate = useNavigate();

    const handleLoginSubmit = async (e) => {
        e.preventDefault();
        // Add your login logic here
        handleLogin();
    };

    return (
        <div className="container">
            <div className="row justify-content-center mt-5">
                <div className="col-md-6">
                    <div className="card shadow" style={{ backgroundImage: `url(${backgroundImage})`, backgroundSize: 'cover', backgroundPosition: 'center', borderRadius: '10px' }}>
                        <div className="card-body" style={{ backgroundColor: 'rgba(255, 255, 255, 0.8)' }}>
                            <h3 className="text-center mb-4">Login</h3>
                            <form onSubmit={handleLoginSubmit}>
                                <div className="mb-3">
                                    <label htmlFor="username" className="form-label">Username</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        id="username"
                                        placeholder="Enter username"
                                        onChange={(e) => setUsername(e.target.value)}
                                        value={username}
                                    />
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="password" className="form-label">Password</label>
                                    <input
                                        type="password"
                                        className="form-control"
                                        id="password"
                                        placeholder="Enter password"
                                        onChange={(e) => setPassword(e.target.value)}
                                        value={password}
                                    />
                                </div>

                                <div className="d-grid gap-2">
                                    <button type="submit" className="btn btn-primary btn-block">
                                        Login
                                    </button>
                                    <Link to="/register" className="btn btn-success btn-block">
                                        Register
                                    </Link>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}
